mapper.select:
SELECT a.user_id, a.friend, a.group_name, a.created_time, a.status, b.nick_name, b.avatar, b.sign FROM tab_relation a JOIN tab_user b ON a.friend = b.user_id


        a.user_id = #{userId, jdbcType=VARCHAR}


        AND a.friend = #{friend, jdbcType=VARCHAR}

mapper.delete:
DELETE FROM tab_relation


        user_id = #{userId, jdbcType=VARCHAR}


        AND friend = #{friend, jdbcType=VARCHAR}
