mapper.select:
SELECT * FROM
(
SELECT A.*,ROWNUM RN
FROM
(
SELECT * FROM WEBCHAT_LOG
ORDER BY TIME
) A
)
WHERE RN BETWEEN #{start} and #{end}

SELECT * FROM
(
SELECT A.*,ROWNUM RN
FROM
(
SELECT * FROM WEBCHAT_LOG
WHERE USERID = #{userid, jdbcType=VARCHAR}
ORDER BY TIME
) A
)
WHERE RN BETWEEN #{start} and #{end}
